<?php
// Dein Base64-codierter String
$base64_string = '';  // (gekürzt)

// Base64-dekodieren
$pdf_decoded = base64_decode($base64_string);

// Dateiname für das PDF
$pdf_file = 'dokument.pdf';

// PDF-Datei speichern
file_put_contents($pdf_file, $pdf_decoded);

echo 'PDF was created and published under <a href="'.$pdf_file.'">'.$pdf_file.'</a>';

